    private void numberStateChanged(javax.swing.event.ChangeEvent evt) {//GEN-FIRST:event_numberStateChanged
        int n = Integer.parseInt(number.getValue().toString());
        int n2 = 1;
        
        while(n>1){
            n2 *= n* (n-1);
            n-=2;
            System.out.println(n2);
        }
        res.setText(Integer.toString(n2));
        
    }//GEN-LAST:event_numberStateChanged

